import { useQuery } from '@tanstack/react-query';

interface GitHubEvent {
  id: string;
  type: string;
  repo: {
    name: string;
  };
  created_at: string;
  payload: any;
}

interface GitHubStats {
  repositories: number;
  followers: number;
  following: number;
  totalStars: number;
  totalForks: number;
}

export function useGitHubActivity() {
  return useQuery({
    queryKey: ['/api/github-activity'],
